export const COUNTER_CODE = `
pragma solidity ^0.5.0;

contract Counter {
  event Increment (uint256 by);

  uint256 public value;

  constructor (uint256 initialValue) public {
    value = initialValue;
  }

  function increment (uint256 by) public {
    value += by;
    emit Increment(by);
  }

  function incrementAndRevert (uint256 by) public {
    increment(by);
    require(false, "incrementAndRevert");
  }
}
`

export const COUNTER_ABI = [
  'event Increment (uint256 by)',
  'constructor (uint256 initialValue)',
  'function increment (uint256 by)',
  'function incrementAndRevert (uint256 by)',
  'function value () view returns (uint256)',
]

